INSERT INTO `Updates` (`IDUpdate`, `ID_ULanguage`, `title`, `version`, `descripcion`, `urlWin`, `urlMac`, `dateUpdate`, `showPopUp`) VALUES ('11', '3', 'Update from 3.0 to 3.2', '3.2', 'Bundle to update Jocomunico from version 3.0 to 3.2. Aside from bug fixes and minor interface changes, such as being able to change the speech rate in the \"Configuration\" menu, this update adds the possibility to add panels with the CACE vocabulary, developed by the University of Barcelona, directly from the \"My panels\" menu.', 'https://jocomunico.com/files/jocomunico-update-3-2.exe', 'https://jocomunico.com/files/jocomunico-mac-update-3-2.zip', '2023-09-22', '1'), ('11', '2', 'Actualización 3.0 a 3.2', '3.2', 'Paquete para actualizar Jocomunico de la versión 3.0 a la 3.2. Las novedades que incluye este paquete son las siguientes: <br/>\r\n <ul>\r\n <li>- Añadidos 185 pictogramas más al vocabulario base tanto en catalán como en castellano.</li>\r\n <li>- Posibilidad de añadir los paneles con el vocabulario CACE desarrollado por la Universidad de Barcelona desde el apartado de \"Mis paneles\".</li>\r\n <li>- Opción para cambiar la velocidad del habla desde el apartado de \"Configuración\".</li>\r\n <li>- Reducido el tiempo que queda abierto el menú de bloqueo con la contraseña a 5 segundos, por si un usuario lo selecciona sin querer que no tenga que esperar tanto para poder continuar utilizando el comunicador.</li>\r\n <li>- Solucionado un error que hacía que algunas frases no se leyeran en voz alta.</li>\r\n <li>- Arreglado un bug en Chrome que no te permitía cambiar el color de una celda mediante el desplegable de \"Editar celda\".</li>\r\n <li>- Actualizados los apartados del Manual de usuario referentes al vocabulario.</li>\r\n <li>- Actualizadas las instrucciones de instalación de actualizaciones en Mac.</li>\r\n <li>- Solucionados otros bugs menores en la interfaz y en el vocabulario.</li>\r\n </ul>', 'https://jocomunico.com/files/jocomunico-update-3-2.exe', 'https://jocomunico.com/files/jocomunico-mac-update-3-2.zip', '2023-09-22', '1'), ('11', '1', 'Actualització 3.0 a 3.2', '3.2', 'Paquet per actualitzar Jocomunico de la versió 3.0 a la 3.2. Les novetats que inclou aquest paquet són les següents: <br/>\r\n <ul>\r\n <li>- Afegits 185 pictogrames més al vocabulari base tant en català com en castellà.</li>\r\n <li>- Possibilitat d\'afegir els panells amb el vocabulari CACE desenvolupat per la Universitat de Barcelona des de l\'apartat \"Els meus panells\".</li>\r\n <li>- Opció per canviar la velocitat de la parla des de l\'apartat de \"Configuració\".</li>\r\n <li>- Reduït el temps que queda obert el menú de bloqueig amb la contrasenya a 5 segons, per si un usuari el selecciona sense voler que no hagi d\'esperar tant per poder continuar utilitzant el comunicador.</li>\r\n <li>- Solucionat un error que feia que algunes frases no es llegissin en veu alta.</li>\r\n <li>- Arreglat un bug a Chrome que no et permetia canviar el color d\'una cel·la mitjançant el desplegable a \"Editar cel·la\".</li>\r\n <li>- Actualitzats els apartats del Manual d\'usuari referents al vocabulari.</li>\r\n <li>- Actualitzades les instruccions d\'instal·lació d\'actualitzacions a Mac.</li>\r\n <li>- Solucionats altres bugs menors a la interfície i al vocabulari.</li>\r\n </ul>', 'https://jocomunico.com/files/jocomunico-update-3-2.exe', 'https://jocomunico.com/files/jocomunico-mac-update-3-2.zip', '2023-09-22', '1'), ('10', '3', 'Jocomunico 3.2', '3.2', 'Bundle with the full version 3.2 of Jocomunico.', 'https://jocomunico.com/files/jocomunico-setup-3-2.exe', 'https://jocomunico.com/files/jocomunico-mac-3-2.zip', '2023-09-22', '1'), ('10', '2', 'Jocomunico 3.2', '3.2', 'Paquete con la versión 3.2 completa de Jocomunico.', 'https://jocomunico.com/files/jocomunico-setup-3-2.exe', 'https://jocomunico.com/files/jocomunico-mac-3-2.zip', '2023-09-22', '1'), ('10', '1', 'Jocomunico 3.2', '3.2', 'Paquet amb la versió 3.2 completa de Jocomunico.', 'https://jocomunico.com/files/jocomunico-setup-3-2.exe', 'https://jocomunico.com/files/jocomunico-mac-3-2.zip', '2023-09-22', '1');
